import React, { Component} from 'react';


class Classcity extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 'Bangalore'};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
      alert('Your City is: ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
        <h1>hello</h1>
      );
    }
  }
  



export default Classcity;
