import React from "react";
import '../css/App.css';

export default class PopUpDisplayBox extends React.Component
{
  constructor(props)
  {
    super(props);
    this.state = {
      popupName : ['Version','City'],
      selectedLocation : props.selectedLocation,
      selectedVersion : props.selectedVersion
    }
  }

  OpenLocationPopUp = () => {
    this.props.changeEntityFlag(true,this.state.selectedLocation,"Location");
  }

  OpenVersionPopUp = () => {
    this.props.changeEntityFlag(true,this.state.selectedVersion,"Version");
  }

  render()
  {
    const popups = this.state.popupName.map(popup => {
      if (popup === "Version") 
      {
        return (
          <div className="popUpDisplay" key={popup} onClick={this.OpenVersionPopUp}>
            <span style={{color: "gray"}}>{popup}</span>
            <p>{
                   this.state.selectedVersion.versionName
                  }
            </p>
          </div>
        )
      }
      else
      {
        return (
          <div className="popUpDisplay" key={popup} onClick={this.OpenLocationPopUp}>
            <span style={{color: "gray"}}>{popup}</span>
            <p>{
                  this.state.selectedLocation
                  }
            </p>
          </div>
        )
      }
    })
    return (
      <div className="popUpContainer">
        {popups}
      </div>
    );
  }

}
