import React from 'react'

export default function PopUp(props) {
    
    const {stateFunc, ListData, Type} = props;

    const SelectionHandler = (option) => {
        console.log(option);
        if(Type == "Version") {
            stateFunc(prevState => ({...prevState, SelectedVersion : option, VersionPopUpVisible : false}))
        }
        else if(Type == "HeaderCities") {
            stateFunc(option);
        }
        else if(Type == "Manufacturers")
            stateFunc(prevState => ({...prevState, ManufacturerPopUpVisible : false, SelectedManufacturer : option}));
        else if(Type == "Cars")
            stateFunc(prevState => ({...prevState, CarPopUpVisible : false, SelectedCarName : option}))
        else
            stateFunc(prevState => ({...prevState, SelectedCity : option, CityPopUpVisible : false}))
    };

    if(Type == "Manufacturers") {
        return (
            <ul id="PopUpList">
                {
                    Object.keys(ListData).map((manu, index) => {
                        return (
                            <li key={index} className = "Manu" onClick = {() => SelectionHandler(manu)}>{manu}</li>
                        )
                        
                    })
                }
            </ul>
        );
    }
    else if (Type == "Cars") {
        return (
            <ul id="PopUpList">
                {
                    ListData.map((car, index) => {
                        return (
                            <li key={index} className = "Manu" onClick = {() => SelectionHandler(car.CarName)}>{car.CarName}</li>
                        )
                        
                    })
                }
            </ul>
        );
    }
    return (
        <ul id = {props.ID ? props.ID : "PopUpList"}>
            {ListData.map((option, index) => {
                if(!option.VersionName)
                    return (<li onClick = {() => SelectionHandler(index)} key = {index}>{option}</li>)
                return (
                    <li onClick = {() => SelectionHandler(option)} key = {index}>
                        <div>
                            <h3>{option.VersionName}</h3>
                        </div>
                        <div style = {{marginTop : "1vh"}}>
                            <p style = {{fontSize : "0.8em"}}>{option.Fuel}    </p>
                            <p style = {{fontSize : "0.8em"}}>{option.Type}</p>
                        </div>
                    </li>
                )
            })}
        </ul>
    )
}
