import React from 'react';

const Cities = {
    CityList : [
    "Alwar", "Jaipur", "Agra", "Ajmer", "Delhi", "Gurgaon", "Mumbai", "Bangalore", "Navi Mumbai", "Pune"
    ],
    SelectedCity : 0
}

const CityReducer = (state = Cities, action) => {
    switch (action.type) {
        case "SELECT_CITY":
            let NewState = {...state, SelectedCity : action.CityID}
            return NewState;
            break;
    
        default:
            break;
    }

    return state;
};

export default CityReducer;
